// Проверить пароль перед отправкой формы
const weakPasswords = data.weak_passwords.examples_for_testing.invalid_passwords;
function validatePassword(password) {
  const rules = data.validation_rules.must_contain;
  return rules.uppercase.required && /[A-ZА-Я]/.test(password) &&
         rules.lowercase.required && /[a-zа-я]/.test(password) &&
         rules.number.required && /\d/.test(password);
}
